var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var bookingSchema = mongoose.Schema({ 
	    salle: String,
        cause: String,
        date: String,
        heuredeb: String,
        heurefin: String,
        equipement: String,
        responsable: String
        
    },{
    versionKey: false
});
mongoose.connect('mongodb://localhost:27017/projet');
module.exports = mongoose.model('bookings', bookingSchema);        
