var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var eventSchema = mongoose.Schema({ 
	    email: String,
        titre: String,
        date: String,
        heuredeb: String,
        heurefin: String,
        lieu: String,
        responsable: String,
        description: String
    },{
    versionKey: false
});
mongoose.connect('mongodb://localhost:27017/projet');
module.exports = mongoose.model('events', eventSchema);        
