var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var userSchema = mongoose.Schema({ 
	email: String, 
	password: String, 
	salt: String,
	info: String,
	adress: String,
	tel: String,
	linkdin: String,
	email2: String,
	name: String
	 },{
    versionKey: false
});
mongoose.connect('mongodb://localhost:27017/projet');
module.exports = mongoose.model('users', userSchema);        
