var mongoose = require('mongoose');
var event = require('config/Models/event');
var mongodb = require('mongodb');

exports.addevent = function(email,titre,date,heuredeb,heurefin,lieu,responsable,description,callback) 
 {

var newevent = new event({ 
	 email: email,
     titre: titre,
     date:date,
     heuredeb:heuredeb,
     heurefin:heurefin,
     lieu:lieu,
     responsable:responsable,
     description:description
    });

    	newevent.save(function (err) {
	     callback({'response':"Event successfully registered"});
        });
   
}