var mongoose = require('mongoose');
var event = require('config/Models/event');
var mongodb = require('mongodb');
exports.deleteevent = function(id,callback)  {
	event.deleteOne({'_id':id},function(err, events) {
        if (err)  {
            callback({'response':err});
                   // response.send(err);
         }
            callback({'response':'Event successfully deleted'});
    });

}
