var mongoose = require('mongoose');
var session = require('config/Models/sessions');
var mongodb = require('mongodb');
exports.deletesession = function(id,callback)  {
	session.deleteOne({'_id':id},function(err, sessions) {
        if (err)  {
            callback({'response':err});
                   // response.send(err);
         }
            callback({'response':'session successfully deleted'});
    });

}
