var mongoose = require('mongoose');
var booking = require('config/Models/booking');
var mongodb = require('mongodb');
exports.getbookingid = function(id,callback)  {
	booking.find({'_id':id},function(err, bookings) {
        if (err)  {
            callback({'response':err});
                   // response.send(err);
         }
            callback({'response':bookings});
    });

}
