var mongoose = require('mongoose');
var session = require('config/Models/sessions');
var mongodb = require('mongodb');
exports.getsession = function(email,callback)  {
	session.find({'email':email},function(err, sessions) {
        if (err)  {
            callback({'response':err});
                   // response.send(err);
         }
            callback({'response':sessions});
    });

}
