var mongoose = require('mongoose');
var user = require('config/Models/user');
var mongodb = require('mongodb');
exports.updateemailuser = function(id,email,callback)  {
 var updateJson = {
              'email':email
            };
	user.update({'_id':id}, {$set: updateJson},function(err, users){ 
        if (err)  {
            callback({'response':err});
         }
            callback({'response':'Email successfully registered'});
    });

}
