var mongoose = require('mongoose');
var event = require('config/Models/event');
var mongodb = require('mongodb');
exports.updateevent = function(id,email,titre,date,heuredeb,heurefin,lieu,responsable,description,callback)  {
 var updateJson = {
              'email':email,
              'titre':titre,
              'date':date,
              'heuredeb':heuredeb,
              'heurefin':heurefin,
              'lieu':lieu,
              'responsable':responsable,
              'description':description
            };
	event.update({'_id':id}, {$set: updateJson},function(err, events){ 
        if (err)  {
            callback({'response':err});
         }
            callback({'response':'event successfully updated'});
    });

}
