var mongoose = require('mongoose');
var user = require('config/Models/user');
var mongodb = require('mongodb');
exports.updateuser = function(email,name,info,adress,tel,linkdin,callback)  {
 var updateJson = {
              'name':name,
              'info':info,
              'adress':adress,
              'tel':tel,
              'linkdin': linkdin
            };
	user.update({'email':email}, {$set: updateJson},function(err, users){ 
        if (err)  {
            callback({'response':err});
         }
            callback({'response':'Name successfully updated'});
    });

}
